// Menu wiring
if (categorySelect){
  fillSubcategories(categorySelect.value || "bolgeler");
  categorySelect.addEventListener("change", () => {
    fillSubcategories(categorySelect.value);
  });
}
if (subCategorySelect){
  subCategorySelect.addEventListener("change", () => {
    if (startBtn) startBtn.disabled = !(["ulkeler","ozellikler","gecitler","ekosistem","tr-ekonomi"].includes(subCategorySelect.value));
  });
}

showMenu();
